<template>
    <div>
        <plugin-search @showResults="showingSearchResults = true" @hideResults="showingSearchResults = false" :plugins="allPlugins"></plugin-search>

        <a href="#" class="category-selector-btn" @click.prevent="showCategorySelector = !showCategorySelector">All categories</a>

        <div class="category-selector" :class="{ hidden: !showCategorySelector }">
            <div class="category-selector-header">
                <a href="#" @click.prevent="showCategorySelector = false">Hide categories</a>
            </div>
            <div class="category-selector-body">
                <ul class="categories">
                    <li v-if="enableCraftId">
                        <router-link to="/upgrade-craft">
                            <img src="data:image/svg+xml;base64," />
                            Upgrade Craft CMS
                        </router-link>
                    </li>
                    <li v-for="category in getAllCategories()">
                        <router-link :to="'/categories/'+category.id">
                            <img :src="category.iconUrl" />
                            {{ category.title }}
                        </router-link>
                    </li>
                </ul>
            </div>
        </div>

        <div v-if="!showingSearchResults" class="ps-grid-wrapper has-sidebar">
            <div class="ps-grid-sidebar categories-wrapper">
                <h2>{{ "Categories"|t('app') }}</h2>
                <ul class="categories">
                    <li v-if="enableCraftId">
                        <router-link to="/upgrade-craft">
                            <img src="data:image/svg+xml;base64," />
                            Upgrade Craft CMS
                        </router-link>
                    </li>
                    <li v-for="category in getAllCategories()">
                        <router-link :to="'/categories/'+category.id">
                            <img :src="category.iconUrl" />
                            {{ category.title }}
                        </router-link>
                    </li>
                </ul>
            </div>

            <div class="ps-grid-main">
                <template v-if="pluginStoreData.featuredPlugins">
                    <template v-for="featuredPlugin in pluginStoreData.featuredPlugins">
                        <router-link class="right" :to="'/featured/'+featuredPlugin.id">See all</router-link>
                        <div>
                            <h2>{{ featuredPlugin.title }}</h2>
                            <plugin-grid :plugins="getPluginsByIds(featuredPlugin.plugins.slice(0, featuredPlugin.limit))"></plugin-grid>
                        </div>
                    </template>
                </template>

                <template v-if="activeTrialPlugins.length > 0">
                    <h2>{{ "Active Trials"|t('app') }}</h2>
                    <plugin-grid :plugins="activeTrialPlugins"></plugin-grid>
                </template>
            </div>
        </div>
    </div>
</template>


<script>
    import { mapGetters } from 'vuex'
    import PluginGrid from './components/PluginGrid';
    import PluginSearch from './components/PluginSearch';

    export default {

        components: {
            PluginGrid,
            PluginSearch,
        },

        data () {
            return {
                showingSearchResults: false,
                showCategorySelector: false,
            }
        },

        computed: {

            ...mapGetters({
                enableCraftId: 'enableCraftId',
                allPlugins: 'allPlugins',
                pluginStoreData: 'pluginStoreData',
                activeTrialPlugins: 'activeTrialPlugins',
                pluginStoreGetAllCategories: 'pluginStoreGetAllCategories',
                getAllCategories: 'getAllCategories',
                getPluginsByIds: 'getPluginsByIds',
                installedPlugins: 'installedPlugins',
            }),

            indexBlocks() {
                return this.pluginStoreData.indexBlocks;
            }

        },

        created() {
            this.$root.pageTitle = 'Plugin Store';
        },

        mounted() {
            this.$root.crumbs = null;
        }

    }
</script>
