        &:hover {
            @apply .no-underline;
        }
    }


    /* Category Selector */

    .category-selector {
        @apply .hidden .flex-col .fixed .pin-t .pin-l .bg-white .z-20;
        width: 100vw;
        height: 100vh;
        box-sizing: border-box;

        .category-selector-header {
            a {
                @apply .block .text-black;
