                    @apply .no-underline;
                }
            }
        }

        .category-selector-body {
            @apply .overflow-auto .h-full;
            box-sizing: border-box;

            ul.categories {
                li {
                    &:first-child a {
                        border-top: 0;
                    }

                    &:first-child:before {
                        @apply .hidden;
                    }

                    &:before,
                    &:after {
                        left: 1rem;
                        right: 1rem;
                    }

                    a {
                        padding-left: 55px;

                        img {
                            left: 24px;
                        }
                    }
                }
            }
        }
    }

    @media only screen and (max-width: 974px) {
        .category-selector-btn {
            @apply .block;
